<script>$(document).ready(function(){	simulateUserClickOnUploadButton = (function (){// I love ugly browser hacks, I'm looking at you here IE (ಠ_ಠ)		var userfile = $('#userfile');		userfile.click(function() {			$('#realfile').click();// Simulate a user click			var checkForFile = setInterval(function(){// Check if the hidden value is set				userfile.val($('#realfile').val());				if (userfile.val() != ''){// Enable upload button					$('#uploadButton').removeAttr('disabled');					clearInterval(checkForFile);				}else{// Disable upload button					$('#uploadButton').attr('disabled','disabled');				}			},700);		});	})();    // Modal window for pictures	$("a[rel='album']").colorbox({transition:"fade"});        $("a.delete").click(function(event) {        event.preventDefault();        if (confirm('Delete this picture ?')){            window.location.href = '/photos/delete/'+$(this).attr('rel');        }    });});</script><div class="clearfix content">    <h3>Photos</h3>    <hr style="border-color: #eee;">    <? foreach ($pictures as $picture): ?>    	<div class="left">            <div class="picture <?=($picture->avatar ? 'on' : '');?>">                <a rel="album" href="/uploads/<?=$picture->thumb?>" class="zoom"><img src="/views/images/zoom_in.png"></a>				<img height="115" width="115" src="/uploads/t/<?=$picture->thumb?>" alt="<?=$picture->id?>">                <div class="picture-menu">                   <a href="/photos/setDefault/<?=$picture->id?>" title="Set default" class="setdefault"><img src="/views/images/pin.png"></a>                   <? /* Can't delete default picture */ ?>                   <? if (!$picture->avatar): ?>                        <a rel="<?=$picture->id?>" title="Delete picture" class="delete"><img src="/views/images/delete.png" alt="Delete picture"></a>                    <? endif; ?>                </div>            </div>        </div>    <? endforeach; ?>    <? if (empty($pictures)): ?>        <div class="warning">You do not have any pictures. You can upload some below.</div>    <? endif; ?>    <br clear="all">    <hr style="border-color: #eee;"><!-- end --></div><br><div class="clearfix content">	<h3>Picture Upload</h3>	<form action="/photos/uploadPicture" enctype="multipart/form-data" method="post">		<input type="file" name="realfile" id="realfile" style="opacity:0;width:0px;-moz-opacity:0;filter:alpha(opacity: 0);"><!-- real input field, but hidden-->		Send this file:		<input class="uibutton icon add" name="userfile" id="userfile" type="button" value="Click here to select a picture to upload" /><!-- fake button -->		<button type="submit" id="uploadButton" class="uibutton icon add" disabled>Upload</button>	</form></div>